import Axios from "axios";

export default class RestDataSource {
  GetData = (dataType, params) => {
    let endpoint;

    switch (dataType) {
      case "products":
        endpoint = process.env.REACT_APP_PRODUCTS_ENDPOINT;
        break;
      case "categories":
        endpoint = process.env.REACT_APP_CATEGORIES_ENDPOINT;
        break;
      default:
        throw new Error(`Unknown datatype ${dataType}`);
    }
    return this.SendRequest("get", endpoint, params);
  }
  SendRequest = (method, url, params) => Axios.request({ method, url });
}
