import React, { Component } from 'react';
import { Route, Link } from 'react-router-dom';

export default class ToggleLink extends Component {
  render() {
    const {
      to, exact, children, className, activeClass, inActiveClass,
    } = this.props;

    return (
      <Route
        path={to}
        exact={exact}
        children={(routeProps) => {
          const baseClasses = className || 'm-2 btn btn-block';
          const activeClass = activeClass || 'btn-primary';
          const inActiveClass = inActiveClass || 'btn-secondary';
          const combinedClasses = `${baseClasses} ${routeProps.match ? activeClass : inActiveClass}`;
          return (
            <Link
              to={to}
              className={combinedClasses}
            >
              {children}
            </Link>
          );
        }}
      />
    );
  }
}
